
public class PerfectSquare {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				
		for(int i=1;i<=20;i++){
			
			
		double root=Math.sqrt(i);	
		if(root==Math.ceil(root)){
			
			System.out.println(i+"\t is a Perfect number");
			
		}
			
			
			
		}
		
	}

}
