
public class Traingle3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int size=5;
		for(int i=1;i<=size;i++){
			
			
			for(int j=size;j>=1;j--){
				
				if(j>=i){
				System.out.print("*");
				}
				
			}
			
			System.out.println("");
		}
		
	}

}
